import path from 'node:path';
export const fixSourceMapRef = (ctx) => {
    const { contents, originName, filename } = ctx;
    const _contents = originName === filename
        ? contents
        : contents.replace(`//# sourceMappingURL=${path.basename(originName)}.map`, `//# sourceMappingURL=${path.basename(filename)}.map`);
    return Object.assign(Object.assign({}, ctx), { contents: _contents });
};
